import React from 'react';
import './App.css';
import { Upload, message, Button } from 'antd';
import 'antd/dist/antd.css';
import { InboxOutlined } from '@ant-design/icons';

function App() {
  const { Dragger } = Upload;
  const defaultImgSrc = 'data:image/png;base64,';
  const props = {
    accept: '.jpg,.png',
    multiple: false,
    showUploadList: false,
    data: (file: File) => {
      if (!file) {
        return;
      }
      var FR = new FileReader();
      FR.addEventListener("load", function(e) {
        if (e && e.target && e.target.result && typeof e.target.result === 'string') {
          const result = e.target.result;
          const previewImage = document.getElementById("img") as HTMLImageElement;
          if (previewImage) {
            previewImage.src = result;
          }
          const base64PreviewTA = document.getElementById('base64TA') as HTMLTextAreaElement;
          if (base64PreviewTA) {
            base64PreviewTA.innerHTML = result;
          }
        }
      });
      FR.readAsDataURL( file );
    },
  };

  const copyToClipboard = () => {
    const base64TAElement = document.getElementById('base64TA');
    const base64Data = base64TAElement?.innerHTML;
    navigator.clipboard.writeText(base64Data || '').then(function() {
      message.success('Copy to clipboard successful');
    }, function() {
      message.error('Copy to clipboard failed');
    })
  }

  return (
    <div className="app">
      <div className="title">Image to base64</div>
      <div className="head">
        <Dragger {...props}>
          <p className="ant-upload-drag-icon">
            <InboxOutlined />
          </p>
          <p className="ant-upload-text">Click or drag file to this area to upload</p>
        </Dragger>
        <img alt="preview-img" id="img" height="150" src={defaultImgSrc}/>
      </div>
      <textarea id="base64TA" className="text-area" placeholder={'here is image base64 data'} rows={5} />
      <Button className="copy-button" type="primary" shape="round" size={'large'} onClick={copyToClipboard}>
        Copy
      </Button>
    </div>
  );
}

export default App;
